import {createAsyncThunk, createSlice} from "@reduxjs/toolkit";
import {api} from "../../../api";

export type ItoDo = {
    id: number,
    title: string,
    isCompleted: false
}

type InitialState = {
    items: ItoDo[],
    isLoading: boolean,
    error: string
}

const initialState: InitialState = {
    items: [],
    isLoading: false,
    error: ''
}

export const todoReducer = createSlice({
    name: 'todo',
    initialState,
    reducers: {
    },
    extraReducers: builder => {

    }
})

const fetchTodos = createAsyncThunk(
    'todos/fetchTodos',
    async (count : number) => {
        return await api.fetchTodos(3)
    }
)

export default todoReducer.reducer
