import React, {FC, useEffect} from 'react';
import {useAppDispatch, useAppSelector} from "./hooks/redux";
import {addToDo, fetchTodos} from "./store/dacks/toDoReducer";

const App: FC = () => {

    const dispatch = useAppDispatch()
    const {items, isLoading, error} = useAppSelector(state => state.todoReducer)

    useEffect(() => {
        dispatch(fetchTodos(2))
    }, [dispatch])

    const handleAdd = () => {
        dispatch(addToDo("test 33"))
    }

    if (isLoading) {
        return <h2>Loading...</h2>
    }

    if (error) {
        return <h2>{error}</h2>
    }

    return (
        <div>
            <ul>
                {
                    items && items.map(el => (
                        <li key={el.id} className={`${el.isComplete ? "completed" : ""}`}>{el.title}</li>
                    ))
                }
            </ul>

            <button onClick={handleAdd}>Add ToDo</button>
        </div>
    );
};

export default App;
