    title: string,
    isCompleted: false
}

type InitialState = {
    items: Items[],
    isLoading: boolean,
    error: string
}

const initialState: InitialState = {
    items: [],
    isLoading: false,
    error: ''
}

export const todoReducer = createSlice({
