import React, {FC, useEffect, useState} from 'react';
import {useAppDispatch, useAppSelector} from "./hooks/redux";
import {addToDo, fetchTodos, ItoDo} from "./store/dacks/toDoReducer";

const App: FC = () => {

    const dispatch = useAppDispatch()
    const {items, isLoading, error} = useAppSelector(state => state.todoReducer)

    const [newToDo, setNewToDo] = useState<ItoDo>({title: "", isComplete: false})

    useEffect(() => {
        dispatch(fetchTodos(10))
    }, [dispatch])

    const handleAdd = () => {
        dispatch(addToDo(newToDo))
        setNewToDo({title: "", isComplete: false})
    }

    const handleRemove = (id?: number) => {

    }

    if (isLoading) {
        return <h2>Loading...</h2>
    }

    if (error) {
        return <h2>{error}</h2>
    }

    return (
        <div>
            <ul>
                {
                    items && items.map(el => (
                        <li key={el.id} className={`${el.isComplete ? "completed" : ""}`}>
                            {el.title}
                            <button onClick={() => handleRemove(el.id)}>Remove</button>
                        </li>
                    ))
                }
            </ul>
            <input type="text" value={newToDo.title} onChange={(e) => setNewToDo({...newToDo, title: e.target.value})}/>
            <button onClick={handleAdd}>Add ToDo</button>
        </div>
    );
};

export default App;
