import React, {FC, useEffect, useState} from 'react';
import {useAppDispatch, useAppSelector} from "./hooks/redux";
import {addToDo, fetchTodos, ItoDo} from "./store/dacks/toDoReducer";

const App: FC = () => {

    const dispatch = useAppDispatch()
    const {items, isLoading, error} = useAppSelector(state => state.todoReducer)

    const [newToDo, setNewToDo] = useState<ItoDo>({title: "", isComplete: false})

    useEffect(() => {
        dispatch(fetchTodos(2))
    }, [dispatch])

    const handleAdd = (value: string) => {
        dispatch(addToDo({title: value, isComplete: false}))
    }

    if (isLoading) {
        return <h2>Loading...</h2>
    }

    if (error) {
        return <h2>{error}</h2>
    }

    return (
        <div>
            <ul>
                {
                    items && items.map(el => (
                        <li key={el.id} className={`${el.isComplete ? "completed" : ""}`}>{el.title}</li>
                    ))
                }
            </ul>
            <input type="text" on/>
            <button onClick={handleAdd}>Add ToDo</button>
        </div>
    );
};

export default App;
